mapper.select:
        select * from board_table where board_info_id = #{board_info_id}


				and (title like concat('%',#{cri.keyword},'%') or content like concat_ws('%',#{cri.keyword},'%'))


                and title like concat('%',#{cri.keyword},'%')


                and writer like concat('%',#{cri.keyword},'%')


		limit #{cri.start}, #{cri.amount}

mapper.update:
update board_table
set title= #{title},
content=#{content},
writer = #{writer}
where id = #{id}
